      <div>
        <table>

        <tbody>
            { loclist.map((value,key)=>{
              return(
                        <tr key={key}>


                            <td><h2>{value.name}</h2></td>

                            <td> <img className="medium" src={value.image} alt={value.name} /></td>

                        </tr>

            )})} 
            </tbody> 

        </table>
        </div>
  </div>
  )


}
