const validation =(values)=>{
    let errors={}
    if(!values.lang){
        errors.lang="lang is required";
    }else if(!values.type=="number"){
        errors.long="required number";
    }
    if(!values.long){
        errors.lang="long is required";
    }
    if(!values.km){
        errors.lang="km is required";
    }
return errors;
}
export default validation;
